import Prismic from 'prismic-javascript';
import PrismicDOM from 'prismic-dom';
var apiEndpoint = 'https://databorder.cdn.prismic.io/api/v2'

export async function getDocuments(type) {
    return Prismic.getApi(apiEndpoint).then(api => api.query(
        Prismic.Predicates.at('document.type', type),
    ));
}

export function formatHTML(obj) {
    return PrismicDOM.RichText.asHtml(obj)
}